        private void actionviewflipper()
    {
        //Animation animation = AnimationUtils.loadAnimation(getApplicationContext(), R.anim.);
        ArrayList<Integer> mangquangcao = new ArrayList<>();
        mangquangcao.add(R.drawable.anh_1);
        mangquangcao.add(R.drawable.anh_2);
        mangquangcao.add(R.drawable.anh_3);
        mangquangcao.add(R.drawable.anh_4);
        mangquangcao.add(R.drawable.anh_5);
        mangquangcao.add(R.drawable.anh_6);
        for(int i=0;i<mangquangcao.size();i++)
        {
            ImageView imageView = new ImageView(getApplicationContext());
            imageView.setImageResource(mangquangcao.get(i));
            imageView.setScaleType(ImageView.ScaleType.CENTER_CROP);
            viewFlipper.addView(imageView);
        }
        viewFlipper.setFlipInterval(5000);
        viewFlipper.setAutoStart(true);
        Animation animation_right = AnimationUtils.loadAnimation(getApplicationContext(),R.anim.go_right);
        Animation animation_left = AnimationUtils.loadAnimation(getApplicationContext(),R.anim.go_left);
        viewFlipper.setInAnimation(animation_right);
        viewFlipper.setOutAnimation(animation_left);
    }
